# ------------- 工具：判断 blst/pyblst 是否可用 -------------
def blst_available() -> bool:
    try:
        import blst  # noqa: F401
        return True
    except ModuleNotFoundError:
        try:
            import pyblst as blst  # noqa: F401
            return True
        except ModuleNotFoundError:
            return False


